package com.mikkaeru.biometry.controller;

import com.mikkaeru.biometry.repositoty.BiometryRepository;
import com.mikkaeru.helper.IntegrationHelper;
import com.mikkaeru.proposal.model.Proposal;
import com.mikkaeru.request.card.model.Card;
import com.mikkaeru.request.card.repository.CardRepository;
import org.json.JSONObject;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.Arguments;
import org.junit.jupiter.params.provider.MethodSource;
import org.springframework.beans.factory.annotation.Autowired;

import java.math.BigDecimal;
import java.time.LocalDateTime;
import java.util.stream.Stream;

import static java.util.UUID.randomUUID;
import static org.hamcrest.MatcherAssert.assertThat;
import static org.hamcrest.collection.IsCollectionWithSize.hasSize;
import static org.junit.jupiter.params.provider.Arguments.arguments;
import static org.springframework.http.HttpHeaders.LOCATION;
import static org.springframework.http.MediaType.APPLICATION_JSON;
import static org.springframework.test.web.servlet.request.MockMvcRequestBuilders.post;
import static org.springframework.test.web.servlet.result.MockMvcResultMatchers.header;
import static org.springframework.test.web.servlet.result.MockMvcResultMatchers.status;

class BiometryControllerTest extends IntegrationHelper {

    @Autowired
    private CardRepository cardRepository;

    @Autowired
    private BiometryRepository biometryRepository;

    private Proposal proposal;

    private final String cardId = "5555-6666-7777-8884";
    private static final String ENDPOINT = "/cards/{cardCode}/biometrics";

    @BeforeEach
    void setUp() {
        proposal = new Proposal(
                "Proposta 02",
                "proposta2@gmail.com",
                "83525826150",
                new BigDecimal("4000"),
                "Travessa das castanheiras",
                randomUUID().toString());
    }

    @Test
    @DisplayName("Ao fornecer uma base64 valida deve criar a biometria")
    void GIVEN_ValidBase64_MUST_ReturnCreated() throws Exception {
        cardRepository.save(new Card(cardId, proposal.getName(), new BigDecimal("4000"), proposal.getProposalCode(), LocalDateTime.now(), null));

        String fingerprint = "";

        JSONObject json = new JSONObject().put("fingerprint", fingerprint);

        mockMvc.perform(post(ENDPOINT, proposal.getProposalCode())
                .content(json.toString())
                .contentType(APPLICATION_JSON))
                .andExpect(status().isCreated())
                .andExpect(header().exists(LOCATION));

        assertThat(biometryRepository.findAll(), hasSize(1));
    }

    @ParameterizedTest
    @MethodSource("provideInvalidData")
    @DisplayName("Ao fornecer uma base64 invalida deve retornar status code 400")
    void GIVEN_InValidBase64_MUST_ReturnBadRequest(String fingerprint) throws Exception {
        cardRepository.save(new Card(cardId, proposal.getName(), new BigDecimal("4000"), proposal.getProposalCode(), LocalDateTime.now(), null));

        JSONObject json = new JSONObject().put("fingerprint", fingerprint);

        mockMvc.perform(post(ENDPOINT, proposal.getProposalCode())
                .content(json.toString())
                .contentType(APPLICATION_JSON))
                .andExpect(status().isBadRequest());
    }

    private static Stream<Arguments> provideInvalidData() {
        return Stream.of(
                arguments("  "),
                arguments("4AAQSkZJRgABAQAAAQABAAD/DONA.MARIA/4AAQSkZJRgABAQAAAQABAAD")
        );
    }
}